module("Language: fr",{setup:function(){numeral.language("fr")}});test("Format Numbers",15,function(){var e=[[1e4,"0,0.0000","10 000,0000"],[10000.23,"0,0","10 000"],[-1e4,"0,0.0","-10 000,0"],[10000.1234,"0.000","10000,123"],[-1e4,"(0,0.0000)","(10 000,0000)"],[-0.23,".00","-,23"],[-0.23,"(.00)","(,23)"],[.23,"0.00000","0,23000"],[1230974,"0.0a","1,2m"],[1460,"0a","1k"],[-104e3,"0a","-104k"],[1,"0o","1er"],[52,"0o","52ème"],[23,"0o","23ème"],[100,"0o","100ème"]];for(var t=0;t<e.length;t++)strictEqual(numeral(e[t][0]).format(e[t][1]),e[t][2],e[t][1])});test("Format Money",4,function(){var e=[[1000.234,"$0,0.00","€1 000,23"],[-1000.234,"($0,0)","(€1 000)"],[-1000.234,"$0.00","-€1000,23"],[1230974,"($0.00a)","€1,23m"]];for(var t=0;t<e.length;t++)strictEqual(numeral(e[t][0]).format(e[t][1]),e[t][2],e[t][1])});test("Format Percentages",4,function(){var e=[[1,"0%","100%"],[.974878234,"0.000%","97,488%"],[-0.43,"0%","-43%"],[.43,"(0.000%)","43,000%"]];for(var t=0;t<e.length;t++)strictEqual(numeral(e[t][0]).format(e[t][1]),e[t][2],e[t][1])});test("Unformat",9,function(){var e=[["10 000,123",10000.123],["(0,12345)",-0.12345],["(€1,23m)",-123e4],["10k",1e4],["-10k",-1e4],["23ème",23],["€10 000,00",1e4],["-76%",-0.76],["2:23:57",8637]];for(var t=0;t<e.length;t++)strictEqual(numeral().unformat(e[t][0]),e[t][1],e[t][0])});